<?php/** * 促销活动类 */class action{    private $id = NULL;	public function __construct( )	{	}	//添加商品	public function addAction( $vartab )	{        $db = mysql::getInstance();		return DbBase::insertRows('a_action',$vartab);	}		//修改商品	public function editAction( $id, $vartab )	{		$db = mysql::getInstance();		return DbBase::updateByData("a_action" , $id , $vartab , "a_id" );	}		//删除商品	public function delAction( $id,  $flag , $and , $dbname = 'a_action' )	{	   $db = mysql::getInstance();		return $db->DeleteRecord( $dbname, $id , $flag , $and );	}	//通过类型的id得到类型的名称	public function getGoodsTypeNameByGTid($tid,$an='1=1')	{		$db = mysql::getInstance();		if ($tid =='')		{			$an = ($an == '')?$an:" where $an";			$sql = 'SELECT as_id,as_typename FROM a_goodstype'.$an;		}		else		{			$an = ($an == '')?$an:" and $an";			$sql = "SELECT as_id,as_typename FROM a_goodstype WHERE as_id = '{$tid}'".$an;		}		$db->Query($sql);		return $db->getAllRecodes( \PDO::FETCH_ASSOC );	}}